import React, { useState } from "react";
import classNames from "classnames";


import "./style.css"

const CategoryTitle = ( props ) =>{

    const {  categoryTitle, categoryId, updateCategoryId } = props

    //console.log( categoryTitle )

    const [tarIndex,setTarIndex] = useState(-1)

    function onClickHandle( index ){
        updateCategoryId(index)
    }

    return(
        <div id="category-title">
            {
            categoryTitle.length > 0 ? 
            <ul className="list-ul">
                {categoryTitle.map(el =>(
                    <li key={el.block_id} onClick={()=>{onClickHandle(el.category_id)}} className={classNames({"list-navbar":el.category_id !== categoryId, "nav-select":el.category_id === categoryId})}>
                        <span>{el.category_name}</span>
                    </li>
                ))}
            </ul>
            :''}
            
        </div>
    )
}

export default CategoryTitle
