import React, { useState } from "react";
import axios from "axios";
import "./style.css"

const LoginView = () =>{

    const [uname,setUname] = useState("")
    const [upassword,setUpassword] = useState("")
    
    async function loginCheckEvent(){

        const { data, status } = await axios({
            url:"http://127.0.0.1:8000/login/check",
            method:"post",
            headers:{
                "Content-Type":"application/json;charset=utf-8",
                "Accept":"application/json"
            },
            data:{
                uname:"ivan",
                uphone:"18502743855",
                upassword:"zhou123456"
            }
        })
        if( status === 200 ){
            console.log(data)
        }   
    }

    const uNameChangeHandle = (e) =>{
        const { value } = e.target
        setUname( value )
    }

    const uPasswordChangeHandle = (e) =>{
        const { value } = e.target
        setUpassword( value )
    }

    const onSubmitHandle = (e) =>{
        e.prent
    }

    return (

            <div id="login-container">
                <div className="input-container">
                    <i className=" iconfont icon-tablet"/>
                    <input
                        type="text"
                        placeholder="用户名/手机号"
                        value={uname}
                        onChange={uNameChangeHandle}
                    />
                </div>
                <div className="phone-container">
                    <i className="iconfont icon-key"/>
                    <button>发送验证码</button>
                    <input 
                    type="text" 
                    placeholder="输入验证码"
                    value={upassword}
                    onChange={uPasswordChangeHandle}
                    />
                </div>
                <button className="btn-login"  onClick={loginCheckEvent}>登录</button>
            </div>
    )
}

export default LoginView
