import React, { useEffect, useState } from 'react'
import { Swiper, Toast } from 'antd-mobile'

import axios from 'axios'

import  './style.css'



const Banner = () => {
  
  const [pictures,setPicture] = useState([{"id": 178747, "img": "//cdn.cnbj1.fds.api.mi-img.com/mi-mall/3390cdb097bdd207e44a8ce5fff8872f.jpg?thumb=1&w=720&h=360"}, {"id": 537008, "img": "//cdn.cnbj1.fds.api.mi-img.com/mi-mall/44a84a3c6568127dcf3c08119f5b3c60.jpg?thumb=1&w=720&h=360"}, {"id": 620470, "img": "//cdn.cnbj1.fds.api.mi-img.com/mi-mall/b09825ca0bc2c338e8f7d0879328bfb3.png?thumb=1&w=720&h=360"}, {"id": 884080, "img": "//cdn.cnbj1.fds.api.mi-img.com/mi-mall/259389980d37669621ec020db7a77949.png?thumb=1&w=720&h=360"}])


  useEffect(()=>{
    axios.get('http://127.0.0.1:8000/pictures').then((res)=>{
         const { data } = res
         setPicture(data)
         console.log('000',data)
    }).catch((error)=>{
         console.log(error)
    })
  },[])
 
  return (
    <div className='swiper-box'>
       <Swiper
       autoplay
       loop
       defaultIndex={0}
       >
       {
          pictures.length > 0 ?
          pictures.map((item, index) => (
              <Swiper.Item key={item.id}>
                <div
                  className="content"
                  onClick={() => {
                    Toast.show(`你点击了卡片 ${index + 1}`)
                  }}
                >
                  <img src={item.img} alt="" className='img-box'/>
                </div>
              </Swiper.Item>
            ))
         :<div>等待数据加载</div>
     }
       </Swiper>
    </div>
  )
}
export default Banner
