import React, { useEffect, useState } from "react";
import PubHeader from "../../../components/PubHeader";
import CheckBottomNav from "./checkBottomNav";
import CheckOutView from "./checkOutView";

import axios from "axios";
import { useParams } from "react-router-dom";

const CheckOut = () =>{

    let { id } = useParams()

    const [data,setData] = useState({})

    useEffect(()=>{
        axios({
            url:"http://127.0.0.1:8000/order/checkout",
            method:"get",
            params:{ "id": id }
         }).then(res=>{
             console.log(res.data)
             setData(el => res.data)
         })
    },[id])

    // const updateSalesOrder = useCallback((events, obj)=>{
        
    //     obj.user = user.id
    //     obj.status = 1
    //     console.log( obj )
    //     axios.post("http://127.0.0.1:8000/order/events", { "events": events, "data":obj } ).then(res=>{
    //         console.log( res.data )
    //         const { code, item } = res.data
    //         if( code === 500 ){
    //             Navigate(`/checkout/${ item.id }`)
    //         }
    //     })
    // },[])

    return(<div>
        <PubHeader title={"用户结算"}/>
        <CheckBottomNav data = {data}/>
        <CheckOutView 
        address={data.address}
        itemdetails = {data.item_details}
        amount = {data.amount}
        />
    </div>)    
}

export default CheckOut
