import React, { useCallback, useEffect, useState , useSyncCallback } from 'react'

import { useDispatch ,useSelector} from "react-redux";

import axios from "axios";

import SearchList from './searchList'
import SearchHeader from './searchHeader'
import LearnMore from '../../../components/LearnMore'

import { updataStartIndex } from '../../../redux/actions/search';

const Search = () =>{

    const dispatch = useDispatch()
    const [items,setItems] = useState([])
    const [exitMore,setExitMore] = useState(true)
    const startIndex = useSelector(state => state.search.startIndex)


    const loadMoreHandle = useSyncCallback( () =>{
            
            console.log(startIndex)
            
            axios.get("http://127.0.0.1:8000/items/",
            {
                params:{
                    startindex:startIndex,
                    items_length:4
                }
            }
            ).then((res)=>{
                const { data } = res
                if( data ){
                    
                    let exitMore = data.exit_more || false
                    let houseList = data.house_list || []
                    dispatch(
                        updataStartIndex(
                            Math.max.apply(Math,houseList.map(el => Number( el.id ) ))
                        )
                    ) 
                    setItems( pre =>[...pre,...houseList])
                    setExitMore( exitMore )
                } 
            })
    })
    //加载记录之后执行加载图片数据
    useEffect(()=>{
        loadMoreHandle()
    },[])

    const onLoadMore = useCallback(()=>{
        return loadMoreHandle()
    },[])
    return(
        <div>

            <SearchHeader/>
            <SearchList items = {items}/>
            {
                exitMore ?
                <LearnMore onLoadMore = { onLoadMore } /> :
                ''
            }
            
        </div>
    )
}

export default Search
