import React, { useState } from 'react'

import './style.css'

const TopInput = () =>{

    const [keyword,setKeyword] = useState('')

    function onInputChange(e){
        const { value } = e.target
        setKeyword( value )
        console.log( keyword )
    } 

    return(
            <input 
            className='top-input' 
            type="text"
            onChange={onInputChange}/>
    )
}

export default TopInput
