import React, {uses} from "react";
import PubHeader from "../../../components/PubHeader";
import BottomBtn from "../../../components/BottomBtn";
import AddressUpdate from "./addressUpdate";

import './style.css'

const Address = () =>{

    const [updateViewFlag,setUpdateViewFlag] = useState(false)

    function onClickHandle(){
        setUpdateViewFlag(!updateViewFlag)
    }

    return(<div id="address-box">
        { updateViewFlag ? <AddressUpdate/> : ''}
         <PubHeader title={"收货地址"}/>
         <BottomBtn title={"添加收货地址"} onClickHandle={onClickHandle}>
             <i className="insert-icon">+ </i>
         </BottomBtn>
    </div>)
}

export default Address
