import React, {useState, useEffect} from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import PubHeader from "../../../components/PubHeader";
import BottomBtn from "../../../components/BottomBtn";

import axios from 'axios'

import './style.css'

const Address = () =>{


    const { user } = useSelector(state => state.login)
    console.log( user )

    const Navigate = useNavigate()

    useEffect(()=>{
        if( !user.token || !user.nick ){
          Navigate("/login")
        }else{
            axios.get('http://127.0.0.1:8000/address/list',{params:{user:user.id}}).then(res=>{
                const { data } = res
                console.log( data )
            })
        }
    },[])



    async function updateAddress( {type, county, addressDetails, phone, consignee} ){
        console.log(county)
        const { data, status } = await axios({
            url:"http://127.0.0.1:8000/address/update",
            method:"post",
            headers:{
                "Content-Type":"application/json;charset=utf-8",
                "Accept":"application/json"
            },
            data:{
                type:type,
                user:user.id,
                province:'0',
                city:'0',
                county:county,
                details_addresss:addressDetails,
                phone:phone,
                consignee:consignee
            }
        })
        console.log(data,status)
        if( status === 200 && data.code === 500 ){
            onClickHandle()
        }
    }

    return(<div id="address-box">
        { updateViewFlag ? <AddressUpdate updateAddress={updateAddress}/> : ''}
         <PubHeader title={"收货地址"}/>
         <BottomBtn title={"添加收货地址"} onClickHandle={onClickHandle}>
             <i className="insert-icon">+ </i>
         </BottomBtn>
    </div>)
}

export default Address
