import React, { useState } from "react";
import classNames from "classnames";


import "./style.css"

const CategoryTitle = ( props ) =>{

    const {  categoryTitle } = props
    const [tarIndex,setTarIndex] = useState(-1)


    return(
        <div id="category-title">
            {
            categoryTitle.length > 0 ? 
            <ul className="list-ul">
                {categoryTitle.map(el =>(
                    <li key={el.block_id}  className={classNames({"list-navbar":el.category_id !== tarIndex, "nav-select":el.category_id === tarIndex})}>
                        <span>{el.category_name}</span>
                    </li>
                ))}
            </ul>
            :''}
            
        </div>
    )
}

export default CategoryTitle
