import React, {useEffect} from "react";
import { useSelector } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import AddressUpdateView from "./addressUpdateView";

import axios from 'axios'

import './style.css'

const AddressUpdate = () =>{



    const { user } = useSelector(state => state.login)
    console.log( user )

    const Navigate = useNavigate()
    const location = useLocation()

    useEffect(()=>{
        // const { state } = location
        // console.log( state )
        if( !user.token || !user.nick ){
          Navigate("/login")
        }
    },[])



    async function updateAddress( {type, county, addressDetails, phone, consignee} ){
        console.log(county)
        const { data, status } = await axios({
            url:"http://127.0.0.1:8000/address/update",
            method:"post",
            headers:{
                "Content-Type":"application/json;charset=utf-8",
                "Accept":"application/json"
            },
            data:{
                type:type,
                user:user.id,
                province:'0',
                city:'0',
                county:county,
                details_addresss:addressDetails,
                phone:phone,
                consignee:consignee
            }
        })
        console.log(data,status)
        if( status === 200 && data.code === 500 ){
            Navigate("-1")
        }
    }

    return(<div id="address-update-box">
       <AddressUpdateView updateAddress={updateAddress}/>
    </div>)
}

export default AddressUpdate
