import React, { useCallback, useEffect, useState } from "react";
import CityHeader from "../../../components/PubHeader/index"
import Curcity from "./curCity";
import CityList from "./CityList";
import { useNavigate } from "react-router-dom";
import { useSelector  } from "react-redux";
import { useDispatch } from "react-redux";


//import { INIT_CITY } from "../../../redux/constants";


const City = () =>{
    
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const cityName = useSelector(state => state.city.city.name)
     
    const [city,setCity] = useState(cityName)
    const onCityChange = useCallback((tarCity)=>{
        setCity( tarCity )
        navigate('/main/home',{state:{
            city
        }})
    },[])

    const [data,setData] = useState({
        citys:[
            {name:"上海"},
            {name:"深圳"},
            {name:"武汉"},
            {name:"南昌"},
            {name:"大庆"},
            {name:"南京"},
            {name:"天津"},
            {name:"杭州"},
            {name:"郑州"},
            {name:"济南"},
            {name:"青岛"},
            {name:"苏州"},
            {name:"无锡"}
        ]
    })
    useEffect(()=>{
        setData({
            citys:[
                {name:"上海"},
                {name:"深圳"},
                {name:"武汉"},
                {name:"南昌"},
                {name:"大庆"},
                {name:"南京"},
                {name:"天津"},
                {name:"杭州"},
                {name:"郑州"},
                {name:"济南"},
                {name:"青岛"},
                {name:"苏州"},
                {name:"无锡"}
            ]
        })
    },[])
    return (
        <div>
            <CityHeader title={"城市选择"}/>
            <Curcity city = { city }/>
            <CityList 
                data = { data }
                onCityChange = { onCityChange }
            />
        </div>
    )
}

export default City
